mapper.select:
select *
from accumulate_points
where 1 = 1

  and id = #{accumulatePoints.id,jdbcType=VARCHAR}


  and user_id = #{accumulatePoints.userId,jdbcType=VARCHAR}


  and parame_id = #{accumulatePoints.parameId,jdbcType=VARCHAR}


  and points = #{accumulatePoints.points,jdbcType=INTEGER}


  and update_date >= #{startDate,jdbcType=TIMESTAMP}


  and update_date <= #{endDate,jdbcType=TIMESTAMP}
